import React from 'react';
import './App.css';
import images from "./images.json";
import Wrapper from "./components/Wrapper"
import ImageCard from "./components/ImageCard/index"


class App extends React.Component {


  state = {
    images: images,
    score: 0,
    guess: "Click on an image to start playing",
    topScore: 0,
    clickedImages: []
  };

  handleShuffle = id => {
    if (!this.state.clickedImages.includes(id)) {
      this.setState({
        score: this.state.score + 1,
        clickedImages: [...this.state.clickedImages, id]
      });
    }
    this.setState({
      images: images.sort(() => Math.random() - 0.5),
      totalClick: this.state.totalClick + 1
    });
  }



  render(){
  return (
    <div className="App">
      <header className="App-header">
      <h1>
          Clicky Game 
      </h1>
      <h4>Score: {this.state.score}</h4>
      <h4>Top Score: {this.state.topScore}</h4>
      <h4>{this.state.guess}</h4>
      <button onClick = {this.hhandleShuffle}>click to handleShuffle</button>


      <Wrapper>
        {this.state.images.map(image => (
          <ImageCard
            key={image.id}
            id={image.id}
            image={image.image}
          />
        ))}
      </Wrapper>
      </header>


    </div>
  );
}
}

export default App;
